package print_java;

public class HW_Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		//int x=2;
		//int y=2;
		
		int age =4;		
		switch (age) {		
		case 1:
		age=5;
		System.out.println("He is 5");
		break;
		
		case 2:
		age=4;
		System.out.println("He is four");
		break;
		
		default:
		System.out.println("I don't know your age");
		break;
		}
		
		
		
	}

}
