package print_java;

public class HW_single_array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String Grocery[]= new String[4];
		
		Grocery[0]="Milk";
		Grocery[1]="Bread";
		Grocery[2]="Butter";
		Grocery[3]="jam";
		
		{
			System.out.println(Grocery[1]);
		}
		
		for (int item=0; item<Grocery.length; item++)
		
		{
			System.out.println(Grocery[item]);
		}
			
		
			
}

}
