package print_java;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age= 5;
		switch (age) {
				
		case 1:
		System.out.println("Crawl");
		break;
		
		case 2:
		System.out.println("TALK");
		break;
		
		case 3:
		System.out.println("Sing");
		break;
		
		default:
		System.out.println("I dont know your age");
		break;
	}

}
	
}